// Connexion.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Connexion() {
    const [email, setEmail] = useState('');
    const [motDePasse, setMotDePasse] = useState('');

    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        // Ici, ajoutez votre logique de validation de la connexion
        // Si la connexion est réussie, redirigez vers la page d'accueil
        navigate('/accueil'); // Utilisez navigate au lieu de history.push
    };

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Connexion</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="email" 
                        placeholder="E-mail" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password" 
                        placeholder="Mot de passe" 
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                    />
                    <button type="submit">Connexion</button>
                </form>
                <p>
                    <span>Vous n'avez pas de compte ? </span>
                    <a href="/inscription">Inscription</a>
                </p>
            </div>
        </div>
    );
}

export default Connexion;
