import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionTournoi.css';

function InscriptionTournoi() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);

  const handleInscription = async (e) => {
    e.preventDefault();
    setIsError(false); // Réinitialiser l'état d'erreur
    try {
      const response = await axios.post(
        `http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, 
        { equipeId },
        { headers: { 'Content-Type': 'application/json' } }
      );
      setMessage('Équipe ajoutée au tournoi avec succès.');
    } catch (error) {
      setIsError(true); // Définir l'état d'erreur sur vrai
      setMessage('Erreur lors de l\'inscription: ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="form-container">
          <h2>Inscription à un Tournoi</h2>
          {message && <p className={isError ? "error-message" : "success-message"}>{message}</p>}
          <form onSubmit={handleInscription}>
            <input
              type="number"
              placeholder="ID du Tournoi"
              value={tournoiId}
              onChange={(e) => setTournoiId(e.target.value)}
            />
            <input
              type="number"
              placeholder="ID de l'Équipe"
              value={equipeId}
              onChange={(e) => setEquipeId(e.target.value)}
            />
            <button type="submit">Inscrire l'équipe</button>
          </form>
        </div>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionTournoi;
