// Inscription.js
import React, { useState } from 'react';
import '../Style/Inscription.css'; // Assurez-vous de créer ce fichier et d'y ajouter les styles appropriés
import logoConnexion from '../Images/LOGO2.png'; 

function Inscription() {
    // Définissez votre état et vos gestionnaires ici
    const [mail, setMail] = useState('');
    const [MotDePasse, setMotDePasse] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Objets de configuration pour la requête POST
        const config = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ Mail, MotDePasse }),
        };

        try {
            // Remplacez 'http://localhost:5000' par l'URL de base de votre API
            const response = await fetch('http://localhost:5000/api/inscription', config);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            // Gérez la réponse - par exemple, stockez le token, redirigez l'utilisateur, etc.
            console.log(data);
        } catch (error) {
            // Gérez les erreurs ici
            console.error("Une erreur s'est produite pendant l'inscription", error);
        }
    };
    return (
        <div className="form-container">
            <div className="logo-container">
                <img src={logoConnexion} alt="Logo Connexion" />
            </div>
            <h2>Inscription</h2>
            <input type="text" placeholder="Nom" />
            <input type="text" placeholder="Prenom" />
            <input type="MotDePasse" placeholder="Mot de passe" />

            <input type="Mail" placeholder="E-mail" />
            <button>Inscription</button>
            <p>
                <span> Vous avez déjà un compte ? </span>
                <br></br>
                <a href="/Connexion">Connexion</a>
            </p>
        </div>
    );
}

export default Inscription;



