import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import { useNavigate } from 'react-router-dom';
import Slider from 'react-slick'; // Importez Slider de react-slick
import '../Style/Accueil.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

function Accueil() {
    const [utilisateur, setUtilisateur] = useState({
        nom: '',
        prenom: '',
    });
    const navigate = useNavigate(); // Utilisé pour rediriger l'utilisateur

    useEffect(() => {
        console.log('Effect exécuté');
        const nomLocal = localStorage.getItem('nom');
        const prenomLocal = localStorage.getItem('prenom');

        if (nomLocal && prenomLocal) {
            setUtilisateur({
                nom: nomLocal,
                prenom: prenomLocal
            });
        } else {
            const email = localStorage.getItem('email');
            console.log('Email extrait du localStorage:', email);
            if (email) {
                const fetchUserData = async () => {
                    try {
                        const response = await axios.get(`http://localhost:5002/api/utilisateur/getNomPrenomParEmail?email=${email}`);
                        console.log('Réponse de l\'API:', response.data);
                        localStorage.setItem('nom', response.data.nom);
                        localStorage.setItem('prenom', response.data.prenom);
                        setUtilisateur({
                            nom: response.data.nom,
                            prenom: response.data.prenom
                        });
                    } catch (error) {
                        console.error('Erreur lors de la récupération des données utilisateur:', error);
                    }
                };

                fetchUserData();
            }
        }
    }, []);

    const handleLogout = () => {
        if (window.confirm("Êtes-vous sûr de vouloir vous déconnecter ?")) {
            // Effacer les données de l'utilisateur du localStorage
            localStorage.removeItem('nom');
            localStorage.removeItem('prenom');
            localStorage.removeItem('email');
            // Rediriger l'utilisateur vers la page de connexion
            navigate('/connexion');
        }
    };
const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
    };

    return (
        <>
            <Navbar />
            <div className="rectangle">
                {utilisateur.nom && utilisateur.prenom ? (
                    <>
                        <div className="texte-sur-image">Bonjour {utilisateur.prenom} {utilisateur.nom}</div>
                        <button onClick={handleLogout} className="logout-button">Se déconnecter</button>
                    </>
                ) : (
                    <div className="texte-sur-image">Chargement...</div>
                )}
                {/* Carrousel d'images */}
                <Slider {...settings}>
                    <div><img src="../Images/PadelGo1.png" alt="Image 1" /></div>
                    <div><img src="/chemin/vers/image2.jpg" alt="Image 2" /></div>
                    <div><img src="/chemin/vers/image3.jpg" alt="Image 3" /></div>
                    {/* Ajoutez autant de <div> que vous avez d'images */}
                </Slider>
            </div>
            <Tapbar />
        </>
    );
}

export default Accueil;
