import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionTournoi.css';

function InscriptionTournoi() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false); // Nouvelle variable d'état pour suivre si le message est une erreur

  const handleInscription = async (e) => {
    e.preventDefault();
    setIsError(false); // Réinitialisez l'état d'erreur à false avant de tenter l'inscription
    try {
      // Assurez-vous que le format de la requête correspond à ce que le backend attend
      const response = await axios.post(
        `http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, 
        { equipeId: equipeId }, // Vérifiez que vous envoyez un objet si c'est ce que le backend attend
        { headers: { 'Content-Type': 'application/json' }}
      );

      setMessage('Équipe ajoutée au tournoi avec succès.');
      setIsError(false); // L'opération a réussi, donc ce n'est pas une erreur
    } catch (error) {
      setMessage('Erreur lors de l\'inscription: ' + (error.response?.data?.message || error.message));
      setIsError(true); // Il y a eu une erreur, on le signale avec la variable d'état
    }
  };

  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="form-container">
          <h2>Inscription à un Tournoi</h2>
          <form onSubmit={handleInscription}>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID du Tournoi"
                value={tournoiId}
                onChange={(e) => setTournoiId(e.target.value)}
              />
            </div>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID de l'Équipe"
                value={equipeId}
                onChange={(e) => setEquipeId(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="submit-button" type="submit">Inscrire l'équipe</button>
            </div>
            {message && <p className={isError ? "error-message" : "success-message"}>{message}</p>}
          </form>
        </div>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionTournoi;
