// Accueil.js

import '../Style/Accueil.css';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import React, { useEffect, useState } from 'react';


function Accueil() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');

    useEffect(() => {
        // Récupèrez le nom et le prénom du local storage
        setNom(localStorage.getItem('nom'));
        setPrenom(localStorage.getItem('prenom'));
    }, []);
    return (
        <>
            <Navbar />
            <div className="rectangle">
                {/* Affichez le message de bienvenue si le nom et le prénom sont définis */}
                {nom && prenom && (
                    <div className="texte-sur-image">Bonjour {prenom} {nom}</div>
                )}
            </div>
            <Tapbar />
        </>
    );
}

export default Accueil;
