// Inscription.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Inscription() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [mail, setEmail] = useState('');

    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        // Ici, ajoutez votre logique de validation et d'inscription
        // Exemple de validation basique (à étendre selon vos besoins)
        if (!nom || !prenom || !motDePasse || !mail) {
            alert("Veuillez remplir tous les champs.");
            return;
        }

        // Si tout est valide, redirigez vers la page d'accueil
        navigate('/accueil'); // Assurez-vous que le chemin est correct
    };

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Inscription</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="text"
                        placeholder="Nom"
                        value={nom}
                        onChange={(e) => setNom(e.target.value)}
                    />
                    <input 
                        type="text"
                        placeholder="Prenom"
                        value={prenom}
                        onChange={(e) => setPrenom(e.target.value)}
                    />
                    <input 
                        type="email"
                        placeholder="E-mail"
                        value={mail}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password"
                        placeholder="Mot de passe"
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                    /> 
                    <button type="submit">Inscription</button>
                </form>
                <p>
                    <span>Vous avez déjà un compte ? </span>
                    <br />
                    <a href="/Connexion">Connexion</a>
                </p>
            </div>
        </div>
    );
}

export default Inscription;
