import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';

function Accueil() {
    const [utilisateur, setUtilisateur] = useState({ nom: '', prenom: '' });

    useEffect(() => {
        // Récupérer l'email du stockage local
        const email = localStorage.getItem('email');
        
        if (email) {
            const fetchUserData = async () => {
                try {
                    const response = await axios.get(`http://localhost:5002/api/utilisateur/getNomPrenomParEmail?email=${email}`);
                    setUtilisateur({
                        nom: response.data.nom,
                        prenom: response.data.prenom
                    });
                } catch (error) {
                    console.error('Erreur lors de la récupération des données utilisateur:', error);// Gérer les erreurs ici
                }
            };

            fetchUserData();
        }
    }, []);

    return (
        <>
            <Navbar />
            <div className="rectangle">
                {utilisateur.nom && utilisateur.prenom ? (
                    <div className="texte-sur-image">Bonjour {utilisateur.prenom} {utilisateur.nom}</div>
                ) : (
                    <p>Chargement...</p> // Ou toute autre gestion du cas où les données ne sont pas encore chargées
                )}
            </div>
            <Tapbar />
        </>
    );
}

export default Accueil;
