import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';

function Accueil() {
    const [utilisateur, setUtilisateur] = useState({ nom: '', prenom: '' });
    const location = useLocation();

    useEffect(() => {
        // Si l'email est passé en state, utilisez-le pour récupérer le nom et le prénom
        const email = location.state?.email;
        if (email) {
            const fetchUserData = async () => {
                try {
                    const response = await axios.get(`http://localhost:5002/api/utilisateur/getNomPrenomParEmail?email=${email}`);

                    setUtilisateur({
                        nom: response.data.nom,
                        prenom: response.data.prenom
                    });
                } catch (error) {
                    console.error('Erreur lors de la récupération des données utilisateur:', error);
                    // Gérer l'erreur
                }
            };

            fetchUserData();
        }
    }, [location]);
    console.log(utilisateur.nom)

    return (
        <>
            <>
    <Navbar />
    <div>Bonjour</div>
    <Tapbar />


</>

        </>
    );
}

export default Accueil;
