// Tournoi.js
import React, { useState, useEffect } from 'react';
import '../Style/Tournoi.css';
import logoConnexion from '../Images/LOGO2.png'; 

function Tournoi() {
    const [tournois, setTournois] = useState([]);

    useEffect(() => {
        // Ici, je ferai une requête à votre API pour récupérer les tournois
        // Pour l'exemple, nous utiliserons des données fictives
        const tournoisFictifs = [
            { id: 1, categorie: "P250", date: "2024-07-10" },
            { id: 2, categorie: "P250", date: "2024-07-12" },
            { id: 3, categorie: "P250", date: "2024-07-1" },
            { id: 4, categorie: "P250", date: "2024-07-11" },
            // ... plus de tournois
        ];
        setTournois(tournoisFictifs);
    }, []);

    return (
        <div className="tournois-container">
            {tournois.map(tournoi => (
                <div key={tournoi.id} className="tournoi-card">
                    <div className="tournoi-info">
                        <h3>{tournoi.categorie}</h3>
                        <p>{tournoi.date}</p>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Tournoi;
