import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionTournoi.css'; // Assurez-vous que le chemin est correct

function InscriptionFinale() {
    const [tournoiId, setTournoiId] = useState('');
    const [equipeId, setEquipeId] = useState('');
    const [erreur, setErreur] = useState('');
    const navigate = useNavigate();

    const handleInscription = async (e) => {
    e.preventDefault();
    setErreur('');

    if (!tournoiId || !equipeId) {
        setErreur('Les deux champs sont obligatoires.');
        return;
    }

    // Créer un objet pour le corps de la requête
    const equipeData = {
        equipeId: parseInt(equipeId, 10) // Assurez-vous que l'ID de l'équipe est un nombre
    };

    try {
        // Envoyer la requête POST à l'API avec le Content-Type défini sur application/json
        const response = await axios.post(`http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, equipeData, {
            headers: {
                'Content-Type': 'application/json'
            }
        });
        // Traitement en cas de succès
        navigate('/accueiladmin'); // Redirection vers la page d'accueil admin
    } catch (error) {
        // Gestion des erreurs
        if (error.response) {
            // Erreur côté serveur
            setErreur(`Erreur lors de l'inscription : ${error.response.data}`);
        } else {
            // Erreur côté client ou réseau
            setErreur("Une erreur est survenue lors de l'inscription.");
        }
    }
};


    return (
        <>
            <Navbar />
            <div className="form-container">
                <h2>S'inscrire à un Tournoi</h2>
                {erreur && <div className="error-message">{erreur}</div>}
                <form onSubmit={handleInscription}>
                    <input
                        type="number"
                        placeholder="ID du Tournoi"
                        value={tournoiId}
                        onChange={e => setTournoiId(e.target.value)}
                    />
                    <input
                        type="number"
                        placeholder="ID de l'Équipe"
                        value={equipeId}
                        onChange={e => setEquipeId(e.target.value)}
                    />
                    <button type="submit">S'inscrire</button>
                </form>
            </div>
            <Tapbar />
        </>
    );
}

export default InscriptionFinale;
