import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Importez axios
import '../Style/Connexion.css'; // Assurez-vous que le chemin est correct
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Connexion() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Remplacez l'URL par celle de votre API de connexion
            const response = await axios.post('http://localhost:4000/api/connexion', {
                email: email,
                password: password,
            });

            // Si la connexion est réussie, redirigez vers la page d'accueil
            // ou une autre page selon la réponse de votre back-end
            navigate('/accueil'); // Assurez-vous que le chemin est correct
        } catch (error) {
            console.error("Une erreur est survenue lors de la connexion", error);
            // Gérer les erreurs de connexion ici (ex : message d'erreur à l'utilisateur)
        }
    };

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Connexion</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="email" 
                        placeholder="E-mail" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password" 
                        placeholder="Mot de passe" 
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit">Connexion</button>
                </form>
                <p>
                    <span>Vous n'avez pas de compte ? </span>
                    <a href="/inscription">Inscription</a>
                </p>
            </div>
        </div>
    );
}

export default Connexion;
