// Tournoi.js
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Importez useHistory pour la navigation
import '../Style/Tournoi.css';

function Tournoi() {
    const [tournois, setTournois] = useState([]);
    const history = useHistory(); // Utilisez useHistory pour la navigation

    useEffect(() => {
        // ... chargement des données des tournois ...
    }, []);

    // Gestionnaire pour le clic sur une carte de tournoi
    const handleCardClick = (tournoiId) => {
        // Remplacez '/tournoi-details' par la route de votre choix
        // Ajoutez l'ID du tournoi à l'URL si nécessaire
        history.push(`/tournoi-details/${tournoiId}`);
    };

    return (
        <div className="tournois-container">
            {tournois.map(tournoi => (
                <div 
                    key={tournoi.id} 
                    className="tournoi-card" 
                    onClick={() => handleCardClick(tournoi.id)} // Ajoutez le gestionnaire de clic ici
                    tabIndex={0} // Rend la div accessible au clavier
                >
                    <div className="tournoi-info">
                        <h3>{tournoi.categorie}</h3>
                        <p>{tournoi.date}</p>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Tournoi;
