// Inscription.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Style/Inscription.css'; // Assurez-vous que le chemin du fichier CSS est correct
import logoConnexion from '../Images/LOGO2.png'; // Assurez-vous que le chemin vers l'image est correct
import axios from 'axios';

function Inscription() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [email, setEmail] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [error, setError] = useState('');

    const navigate = useNavigate();

    const validateEmail = (email) => {
        return email.match(
            /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        );
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        // Validation basique
        if (!nom.trim() || !prenom.trim() || !email.trim() || !motDePasse.trim()) {
            setError('Veuillez remplir tous les champs.');
            return;
        }

        if (!validateEmail(email)) {
            setError('Veuillez entrer une adresse e-mail valide.');
            return;
        }

        try {
            // L'URL de votre API pour l'inscription
            const url = 'http://localhost:5002/api/utilisateur/inscription'; // Remplacez par l'URL correcte de votre API

            // Corps de la requête POST
            const userData = {
                nom,
                prenom,
                email,
                motDePasse,
            }

            // Envoi de la requête à l'API
            await axios.post(url, userData);
            navigate('/accueil'); // Redirection vers la page d'accueil
        } catch (error) {
            if (error.response) {
                // La réponse du serveur avec une erreur
                setError(error.response.data.message);
            } else {
                setError('Une erreur est survenue lors de l\'inscription.');
            }
        }
    };

    return (
        <div className="page-container">
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Inscription</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <input type="text" placeholder="Nom" value={nom} onChange={(e) => setNom(e.target.value)} />
                    <input type="text" placeholder="Prenom" value={prenom} onChange={(e) => setPrenom(e.target.value)} />
                    <input type="email" placeholder="E-mail" value={email} onChange={(e) => setEmail(e.target.value)} />
                    <input type="password" placeholder="Mot de passe" value={motDePasse} onChange={(e) => setMotDePasse(e.target.value)} />
                    <button type="submit">Inscription</button>
                </form>
                <p>
                    Vous avez déjà un compte ? <a href="/connexion">Connexion</a>
                </p>
            </div>
        </div>
    );
}

export default Inscription;
