import React, { useState } from 'react';
import axios from 'axios';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Inscription() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [email, setEmail] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [error, setError] = useState(''); // Ajout d'un état pour gérer les erreurs

    const handleInscription = async () => {
        // Réinitialiser le message d'erreur
        setError('');

        // Vérification de la présence des valeurs
        if (!nom.trim() || !prenom.trim() || !email.trim() || !motDePasse.trim()) {
            setError('Tous les champs doivent être remplis.');
            return;
        }

        // Validation simple de l'email
        if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
            setError('Adresse e-mail invalide.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5002/api/utilisateur/inscription', {
                Nom: nom,
                Prenom: prenom,
                Mail: email,
                MotDePasse: motDePasse,
            });

            if (response && response.data) {
                console.log('Réponse du serveur:', response.data);
                // Gérer la réponse du serveur ici (redirection, affichage d'un message, etc.)
                // Vous pouvez utiliser navigate('/chemin') si vous avez useNavigate de react-router-dom
            } else {
                console.error('Réponse du serveur non valide:', response);
                setError('Une erreur est survenue. Veuillez réessayer.');
            }
        } catch (error) {
            console.error('Erreur lors de la requête:', error.response ? error.response.data : error.message);
            setError(error.response ? error.response.data.message : 'Une erreur réseau est survenue.');
        }
    };

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Inscription</h2>
                {error && <div className="error">{error}</div>} {/* Affichage du message d'erreur */}
                <input type="text" placeholder="Nom" value={nom} onChange={(e) => setNom(e.target.value)} />
                <input type="text" placeholder="Prenom" value={prenom} onChange={(e) => setPrenom(e.target.value)} />
                <input type="email" placeholder="E-mail" value={email} onChange={(e) => setEmail(e.target.value)} />
                <input type="password" placeholder="Mot de passe" value={motDePasse} onChange={(e) => setMotDePasse(e.target.value)} />
                <button type="button" onClick={handleInscription}>Inscription</button>
                <p>
                    <span>Vous avez déjà un compte ? </span>
                    <br />
                    <a href="/connexion">Connexion</a>
                </p>
            </div>
        </div>
    );
}

export default Inscription;
