import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Style/Inscription.css'; // Assurez-vous que le chemin est correct
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Connexion() {
    const [email, setEmail] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [erreur, setErreur] = useState('');

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await axios.post('http://localhost:5002/api/utilisateur/connexion', {
            Mail: email,
            MotDePasse: motDePasse,
        });

        // Si la connexion est réussie, vous pouvez rediriger ou faire autre chose ici.
        navigate('/accueil');
    } catch (error) {
        // Si c'est une erreur 401, définissez un message d'erreur pour informer l'utilisateur.
        if (error.response && error.response.status === 401) {
            setError('L’e-mail ou le mot de passe ne correspond pas à nos enregistrements. Veuillez réessayer.');
        } else {
            setError('Une erreur s’est produite. Veuillez réessayer plus tard.');
        }
    }
};

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Connexion</h2>
                {erreur && <div className="erreur">{erreur}</div>} {/* Affichage du message d'erreur */}
                <form onSubmit={handleSubmit}>
                    <input 
                        type="email" 
                        placeholder="E-mail" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password" 
                        placeholder="Mot de passe" 
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                    />
                    <button type="submit">Connexion</button>
                </form>
                <p>
                    <span>Vous n'avez pas de compte ? </span>
                    <a href="/inscription">Inscription</a>
                </p>
            </div>
        </div>
    );
}

export default Connexion;
