import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionFinale.css';

function InscriptionTournoi() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');

  const handleInscription = async (e) => {
    e.preventDefault();
    try {
        // Récupérer les détails du tournoi pour vérifier le niveau
        const tournoiResponse = await axios.get(`http://localhost:5002/api/tournoi/${tournoiId}`);
        
        // Vérifiez si le tournoi existe et récupérez son niveau
        if (tournoiResponse.status === 200) {
            const niveau = tournoiResponse.data.niveau; // Utilisez le champ correct retourné par votre API
            
            // Envoi de la requête d'inscription si le niveau du tournoi est P250, P100 ou P25
            if (['P250', 'P100', 'P25'].includes(niveau)) {
                // Le corps de la requête doit être correctement formaté en JSON
                const inscriptionResponse = await axios.post(`http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, JSON.stringify({ equipeId }), {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });

                if (inscriptionResponse.status === 200) {
                    setMessage('Équipe ajoutée au tournoi avec succès.');
                } else {
                    setMessage('Erreur lors de l\'ajout de l\'équipe au tournoi.');
                }
            } else {
                // Le tournoi n'est pas de niveau P250, P100 ou P25 ou les informations ne sont pas récupérables
                setMessage('Inscription enregistrée. Vous recevrez plus tard la validation ou non de l\'inscription.');
            }
        } else {
            setMessage('Erreur lors de la récupération des informations du tournoi.');
        }
    } catch (error) {
        setMessage('Erreur lors de l\'inscription: ' + (error.response?.data || error.message));
    }
};


  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="form-container">
          <h2>Inscription à un Tournoi</h2>
          <form onSubmit={handleInscription}>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID du Tournoi"
                value={tournoiId}
                onChange={(e) => setTournoiId(e.target.value)}
              />
            </div>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID de l'Équipe"
                value={equipeId}
                onChange={(e) => setEquipeId(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="submit-button" type="submit">Inscrire l'équipe</button>
            </div>
            {message && <p className="valid-message">{message}</p>}
          </form>
        </div>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionTournoi;
