import React, { useState, useEffect } from 'react';
import '../Style/Tournoi.css';

function Tournoi() {
    const [tournois, setTournois] = useState([]);

    useEffect(() => {
        // Simuler une requête API
        const tournoisFictifs = [
            // ...vos données de tournois
        ];
        setTournois(tournoisFictifs);
    }, []);

    // Fonction pour gérer le clic sur une carte
    const handleCardClick = (tournoiId) => {
        // Vous pourriez ici rediriger vers une page de détail du tournoi
        // ou gérer l'action de clic autrement
        console.log("Tournoi cliqué:", tournoiId);
    };

    return (
        <div className="tournois-container">
            {tournois.map(tournoi => (
                <button 
                    key={tournoi.id} 
                    className="tournoi-card" 
                    onClick={() => handleCardClick(tournoi.id)}
                >
                    <div className="tournoi-info">
                        <h3>{tournoi.categorie}</h3>
                        <p>{tournoi.date}</p>
                    </div>
                </button>
            ))}
        </div>
    );
}

export default Tournoi;
