import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import Tapbar from './Tapbar';

function Accueil() {
    const [utilisateur, setUtilisateur] = useState({ nom: 'TestNom', prenom: 'TestPrénom' });

    // Supposons que cette fonction est appelée lorsque l'utilisateur se connecte
    const simulateFetchUserData = () => {
        // Simule une réponse de l'API
        setTimeout(() => {
            setUtilisateur({ nom: 'Armengaud', prenom: 'Florian' });
        }, 1000);
    };

    useEffect(() => {
        simulateFetchUserData();
    }, []);

    return (
        <>
            <Navbar />
            <div>
                {utilisateur.nom && utilisateur.prenom ? (
                    <p>Bonjour {utilisateur.prenom} {utilisateur.nom}</p>
                ) : (
                    <p>Chargement des données utilisateur...</p>
                )}
            </div>
            <Tapbar />
        </>
    );
}

export default Accueil;
