import React, { useState } from 'react';
import axios from 'axios';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import Navbar from './Navbar';
import Tapbar from './Tapbar';

function Creation() {
    const [categorie, setCategorie] = useState('');
    const [niveau, setNiveau] = useState('');
    const [date, setDate] = useState('');
    const [nombreEquipe, setNombreEquipe] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault(); // Empêcher le rechargement de la page

        // Créez un objet avec les données du formulaire
        const nouveauTournoi = {
            Categorie: categorie,
            Niveau: niveau,
            Date: date,
            NombreEquipe: parseInt(nombreEquipe, 10), // Assurez-vous que c'est un nombre
        };

        try {
            // Envoyer la requête POST à l'API
            const response = await axios.post('http://localhost:5002/api/tournoi', nouveauTournoi);
            console.log(response.data);
            // Ici, vous pouvez gérer la réponse, par exemple rediriger l'utilisateur ou afficher un message de succès
        } catch (error) {
            console.error('Il y a eu une erreur lors de la création du tournoi:', error.response || error);
            // Ici, vous pouvez gérer l'erreur, par exemple afficher un message d'erreur à l'utilisateur
        }
    };

    return (
        <div className="form-container">
            <Navbar />
            <div className="logo-container">
                <img src={logoConnexion} alt="Logo Connexion" />
            </div>
            <h2>Nouveau Tournoi</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Catégorie"
                    value={categorie}
                    onChange={(e) => setCategorie(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Niveau"
                    value={niveau}
                    onChange={(e) => setNiveau(e.target.value)}
                />
                <input
                    type="date"
                    placeholder="Date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Nombre d'équipes"
                    value={nombreEquipe}
                    onChange={(e) => setNombreEquipe(e.target.value)}
                />
                <button type="submit">Création du tournoi</button>
            </form>
        </div>
    );
}

export default Creation;
