import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionFinale.css';

function InscriptionTournoi() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');

  const handleInscription = async (e) => {
    e.preventDefault();
    try {
        // Utiliser le bon endpoint pour récupérer le niveau du tournoi
        const niveauResponse = await axios.get(`http://localhost:5002/api/tournoi/${tournoiId}/niveau`);
        
        if (niveauResponse.status === 200 && niveauResponse.data) {
            const niveau = niveauResponse.data; // Ajustez si le niveau est enveloppé dans un objet

            // Condition pour les niveaux qui permettent une inscription directe
            if (['P250', 'P100', 'P25'].includes(niveau)) {
                const inscriptionResponse = await axios.post(`http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, JSON.stringify({ equipeId: parseInt(equipeId) }), {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });

                if (inscriptionResponse.status === 200) {
                    setMessage('Équipe ajoutée au tournoi avec succès.');
                } else {
                    setMessage('Erreur lors de l\'ajout de l\'équipe au tournoi.');
                }
            } else {
                setMessage('Inscription enregistrée. Vous recevrez plus tard la validation ou non de l\'inscription.');
            }
        } else {
            setMessage('Impossible de récupérer le niveau du tournoi.');
        }
    } catch (error) {
        setMessage('Erreur lors de l\'inscription: ' + (error.response?.data?.message || error.message));
    }
};


  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="form-container">
          <h2>Inscription à un Tournoi</h2>
          <form onSubmit={handleInscription}>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID du Tournoi"
                value={tournoiId}
                onChange={(e) => setTournoiId(e.target.value)}
              />
            </div>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID de l'Équipe"
                value={equipeId}
                onChange={(e) => setEquipeId(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="submit-button" type="submit">Inscrire l'équipe</button>
            </div>
            {message && <p className="valid-message">{message}</p>}
          </form>
        </div>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionTournoi;
