import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionFinale.css';

function InscriptionTournoi() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');

  const handleInscription = async (e) => {
    e.preventDefault();
    try {
        const tournoiResponse = await axios.get(`http://localhost:5002/api/tournoi/${tournoiId}`);
        
        if (tournoiResponse.status === 200) {
            const niveau = tournoiResponse.data.niveau;
            
            // Assurez-vous que le corps de la requête soit formaté correctement
            const body = JSON.stringify({ equipeId: parseInt(equipeId) });
            
            const inscriptionResponse = await axios.post(`http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, body, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            if (inscriptionResponse.status === 200) {
                // Message de succès pour P250, P100, P25
                if (['P250', 'P100', 'P25'].includes(niveau)) {
                    setMessage('Équipe ajoutée au tournoi avec succès. Vous êtes bien inscrit.');
                } else { // Message différent pour les autres niveaux, mais l'inscription est toujours effectuée
                    setMessage('Équipe ajoutée au tournoi avec succès. Vous recevrez plus tard la validation ou non de l\'inscription.');
                }
            } else {
                setMessage('Erreur lors de l\'ajout de l\'équipe au tournoi.');
            }
        } else {
            setMessage('Erreur lors de la récupération des informations du tournoi.');
        }
    } catch (error) {
        setMessage('Erreur lors de l\'inscription: ' + (error.response?.data || error.message));
    }
};


  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="form-container">
          <h2>Inscription à un Tournoi</h2>
          <form onSubmit={handleInscription}>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID du Tournoi"
                value={tournoiId}
                onChange={(e) => setTournoiId(e.target.value)}
              />
            </div>
            <div className="input-group">
              <input
                className="input-field"
                type="number"
                placeholder="ID de l'Équipe"
                value={equipeId}
                onChange={(e) => setEquipeId(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="submit-button" type="submit">Inscrire l'équipe</button>
            </div>
            {message && <p className="valid-message">{message}</p>}
          </form>
        </div>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionTournoi;
