// Inscription.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Inscription() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [mail, setEmail] = useState('');

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!nom || !prenom || !motDePasse || !mail) {
            alert("Veuillez remplir tous les champs.");
            return;
        }

        try {
            // L'URL de votre API pour l'inscription
            const url = 'http://localhost:7164/api/utilisateur/inscription'; // Remplacez par l'URL correcte de votre API
            // Corps de la requête POST
            const userData = {
                nom,
                prenom,
                motDePasse,
                mail
            }; 
            // Envoi de la requête à l'API
            const response = await axios.post(url, userData);
            // Gérer la réponse ici, par exemple enregistrer un token, informer l'utilisateur, etc.
            navigate('/accueil'); // Assurez-vous que le chemin est correct
        } catch (error) {
            // Gestion des erreurs ici
            console.error("Erreur lors de l'inscription", error.response || error);
            alert("Une erreur est survenue lors de l'inscription.");
        }
    };

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Inscription</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="text"
                        placeholder="Nom"
                        value={nom}
                        onChange={(e) => setNom(e.target.value)}
                    />
                    <input 
                        type="text"
                        placeholder="Prenom"
                        value={prenom}
                        onChange={(e) => setPrenom(e.target.value)}
                    />
                    <input 
                        type="email"
                        placeholder="E-mail"
                        value={mail}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password"
                        placeholder="Mot de passe"
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                    />
                    <button type="submit">Inscription</button>
                </form>
                <p>
                    <span>Vous avez déjà un compte ? </span>
                    <br />
                    <a href="/connexion">Connexion</a>
                </p>
            </div>
        </div>
    );
}

export default Inscription;
