    const handleSubmit = (e) => {
        e.preventDefault();
        // Ici, vous ajoutez votre logique de validation et d'inscription

        // Si tout est valide, redirigez vers la page d'accueil
        history.push('/accueil'); // Remplacez '/accueil' par le chemin de votre page d'accueil
    };

    return (
        <div className="page-container">
            {/* ... le reste du code ... */}
            <form onSubmit={handleSubmit}>
                {/* ... champs du formulaire ... */}
                <button type="submit">Inscription</button>
            </form>
            {/* ... le reste du code ... */}
        </div>
    );
}

export default Inscription;
