// Inscription.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Style/Inscription.css';
import logoConnexion from '../Images/LOGO2.png';
import marque from '../Images/PadelGo.png';

function Inscription() {
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [mail, setEmail] = useState('');

    const handleInscription = async () => {
try {
const response = await axios.post('https://localhost:7164/api/utilisateur/inscription', {
Nom: nom,
Prenom: prenom,
Mail: mail,
MotDePasse: motDePasse,
});
if (response && response.data) {
console.log('Réponse du serveur:', response.data);
// Gérer la réponse du serveur ici (redirection, affichage d'un message, etc.)
} else {
console.error('Réponse du serveur non valide:', response);
// Gérer les erreurs ici (affichage d'un message d'erreur, etc.)
}
} catch (error) {
console.error('Erreur lors de la requête:', error.response ? error.response.data : error.message);
// Gérer les erreurs ici (affichage d'un message d'erreur, etc.)
}
};

    return (
        <div className="page-container">
            <div className="marque-container">
                <img src={marque} alt="Marque PadelGo" />
            </div>
            <div className="form-container">
                <div className="logo-container">
                    <img src={logoConnexion} alt="Logo Connexion" />
                </div>
                <h2>Inscription</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="text"
                        placeholder="Nom"
                        value={nom}
                        onChange={(e) => setNom(e.target.value)}
                    />
                    <input 
                        type="text"
                        placeholder="Prenom"
                        value={prenom}
                        onChange={(e) => setPrenom(e.target.value)}
                    />
                    <input 
                        type="email"
                        placeholder="E-mail"
                        value={mail}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password"
                        placeholder="Mot de passe"
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                    />
                    <button type="submit" onClick ={handleInscription}> Inscription</button>
                </form>
                <p>
                    <span>Vous avez déjà un compte ? </span>
                    <br />
                    <a href="/connexion">Connexion</a>
                </p>
            </div>
        </div>
    );
}

export default Inscription;
