import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Tapbar from './Tapbar';
import '../Style/InscriptionTournoi.css'; // Assurez-vous que ce chemin est correct

function InscriptionFinale() {
  const [tournoiId, setTournoiId] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [message, setMessage] = useState('');

  const handleInscription = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5002/api/tournoi/${tournoiId}/addEquipe`, {
        equipeId: parseInt(equipeId, 10)
      });

      if (response.status === 200) {
        setMessage('Équipe ajoutée au tournoi avec succès.');
      } else {
        setMessage('Erreur lors de l\'ajout de l\'équipe au tournoi.');
      }
    } catch (error) {
      setMessage('Erreur lors de l\'inscription: ' + error.message);
    }
  };

  return (
    <>
     
      <div className="inscription-finale-container">
        <h2>S'inscrire à un tournoi</h2>
        {message && <p>{message}</p>}
        <form onSubmit={handleInscription}>
          <input
            type="text"
            placeholder="ID du Tournoi"
            value={tournoiId}
            onChange={(e) => setTournoiId(e.target.value)}
          />
          <input
            type="text"
            placeholder="ID de l'Équipe"
            value={equipeId}
            onChange={(e) => setEquipeId(e.target.value)}
          />
          <button type="submit">S'inscrire</button>
        </form>
      </div>
      <Tapbar />
    </>
  );
}

export default InscriptionFinale;
